! function (e) {
    e(function () {
        e(".explore").click(function () {
            e(".menu").show(), e(this).hide(), e(".learn-popup").hide(), e(".help-popup").hide()
        }), e(".learn").click(function () {
            e(".explore").show(), e(".learn-popup").show(), e(".help-popup").hide(), e(".menu").hide()
        }), e(".help").click(function () {
            e(".explore").show(), e(".help-popup").show(), e(".learn-popup").hide(), e(".menu").hide()
        }), e(".menu .close-menu").click(function () {
            e(".explore").show(), e(".menu").hide()
        }), e(".learn-popup .close-menu").click(function () {
            e(".learn-popup").hide()
        }), e(".help-popup .close-menu").click(function () {
            e(".help-popup").hide()
        }), e(".clear").click(function () {
            e("textarea.form-control").val("")
        }),
		    e(".ignore-click").click(function(){
        return false;
    })
    })
}(jQuery);